#ifndef I2C_HOST_INTERFACE_H
#define I2C_HOST_INTERFACE_H

#include <stdbool.h>
#ifndef __XC8
#include <stddef.h>
#endif
#include <stdint.h>
#include "i2c_host_types.h"

/**
 * @ingroup i2c_host
 * @struct i2c_host_interface_t 
 * @brief This structure contains the portable interfaces for I2C host.
 */
typedef struct
{   
    /**
    * @brief Pointer to the function that initializes the I2C host, setting up any necessary hardware or software resources.
    */
    void (*Initialize)(void);
    
    /**
    * @brief Pointer to the function that deinitializes the I2C host, by resetting the I2C registers to Power On Default (POR) values.
    */
    void (*Deinitialize)(void);

    /**
    * @brief Pointer to the write function of the I2C host.
    */
    bool (*Write)(uint16_t address, uint8_t *data, size_t dataLength);

    /**
    * @brief Pointer to the read function of the I2C host.
    */
    bool (*Read)(uint16_t address, uint8_t *data, size_t dataLength);

    /**
    * @brief Pointer to the write and read function of the I2C host.
    */
    bool (*WriteRead)(uint16_t address, uint8_t *writeData, size_t writeLength, uint8_t *readData, size_t readLength);

    /**
    * @brief Pointer to the function that sets up the transfer parameters of the I2C host.
    */
    bool (*TransferSetup)(struct i2c_transfer_setup* setup, uint32_t srcClkFreq);

    /**
    * @brief Pointer to the function that returns the last error that occurred on the I2C bus.
    */
    i2c_host_error_t (*ErrorGet)(void);

    /**
    * @brief Pointer to the function that checks if the I2C bus is busy.
    */
    bool (*IsBusy)(void);

    /**
    * @brief Pointer to the function that Registers a callback function for I2C events.
    */
    void (*CallbackRegister)(void (*callback)(void));

    /**
    * @brief Pointer to the function that is polled for I2C host events.
    */
    void (*Tasks)(void);
}i2c_host_interface_t;

#endif /* I2C_HOST_INTERFACE_H */